import React, { Component } from 'react';

export default class Add extends Component {
  state = {
    text: ""
  }

  onChangeHandler = (event)=>{
this.setState({
  text : event.target.value
})
  }
  addElement=()=>{
this.props.add(this.state.text)
  }
  selectElement =(event)=>{
    console.log(event.target);
  }
  render() {
    return (
      <div style={{ border: '3px orange solid' }}>
        <h6>Add</h6>
        <input onChange={this.onChangeHandler}></input>
        <select onChange={this.selectElement}>
        <option selected="selected" hidden>Repo state (Private/Public)</option>
          <option  value="Public" >Public</option>

          <option value="Public"  >Private</option>

        </select>
        <button onClick={this.addElement}>add</button>
      </div>
    );
  }
}
