       <td>
         {data.status.toUpperCase()}
       </td>
       <td>
        <label>check</label>
      <input type="checkbox" checked={this.state.isChecked? "checked" : "" } onChange={this.checkHandler} ></input>
       </td>
       <td>
         {data.status? "priavte" : "aa" }
       </td>
       <td>
         {data.language}
       </td>
       <td>
      <button style={{backgroundColor:'Red'}} onClick={deleteHandler.bind(this,data.id)}>X</button>
      </td>
      </tr>

    );
  }
}
